#' Growth Curve Comparison
#'
#' 
#' @param data .
#' @param deps .
#' @param time .
#' @param model .
#' @param pConstraint .
#' @param agg .
#' @param trim .
#' @param tPerc .
#' @param aic .
#' @param aicc .
#' @param bic .
#' @param r2 .
#' @param fTest .
#' @param res .
#' @param calcAction .
#' @param plotKe .
#' @param intInterval .
#' @param intL .
#' @param intU .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$pTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fitq} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$action} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$aplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$pTable$asDF}
#'
#' \code{as.data.frame(results$pTable)}
#'
#' @export
mcurve <- function(
    data,
    deps,
    time,
    model = "richards",
    pConstraint = "strict",
    agg = "mean",
    trim = FALSE,
    tPerc = 10,
    aic = FALSE,
    aicc = FALSE,
    bic = FALSE,
    r2 = FALSE,
    fTest = FALSE,
    res = 100,
    calcAction = FALSE,
    plotKe = FALSE,
    intInterval = FALSE,
    intL = 0,
    intU = 0) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("mcurve requires jmvcore to be installed (restart may be required)")

    if ( ! missing(deps)) deps <- jmvcore::resolveQuo(jmvcore::enquo(deps))
    if ( ! missing(time)) time <- jmvcore::resolveQuo(jmvcore::enquo(time))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(deps), deps, NULL),
            `if`( ! missing(time), time, NULL))


    options <- mcurveOptions$new(
        deps = deps,
        time = time,
        model = model,
        pConstraint = pConstraint,
        agg = agg,
        trim = trim,
        tPerc = tPerc,
        aic = aic,
        aicc = aicc,
        bic = bic,
        r2 = r2,
        fTest = fTest,
        res = res,
        calcAction = calcAction,
        plotKe = plotKe,
        intInterval = intInterval,
        intL = intL,
        intU = intU)

    analysis <- mcurveClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

