#' Growth Curve Modeling
#'
#' 
#' @param data .
#' @param dep .
#' @param time .
#' @param model .
#' @param eq .
#' @param par .
#' @param aic .
#' @param aicc .
#' @param bic .
#' @param r2 .
#' @param r2_adj .
#' @param rmse .
#' @param mae .
#' @param medae .
#' @param smape .
#' @param rrmse .
#' @param ogf .
#' @param ogf_s .
#' @param sndPlot .
#' @param keyGrowth .
#' @param res .
#' @param fPoints .
#' @param pPoints .
#' @param lagEnd .
#' @param thVal .
#' @param asymptote .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$pTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fitq} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fpoints} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$dplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$pTable$asDF}
#'
#' \code{as.data.frame(results$pTable)}
#'
#' @export
scurve <- function(
    data,
    dep,
    time,
    model = "richards",
    eq = TRUE,
    par = TRUE,
    aic = FALSE,
    aicc = FALSE,
    bic = FALSE,
    r2 = FALSE,
    r2_adj = FALSE,
    rmse = FALSE,
    mae = FALSE,
    medae = FALSE,
    smape = FALSE,
    rrmse = FALSE,
    ogf = FALSE,
    ogf_s = FALSE,
    sndPlot = FALSE,
    keyGrowth = TRUE,
    res = 100,
    fPoints = NULL,
    pPoints = NULL,
    lagEnd = NULL,
    thVal = 0.01,
    asymptote = NULL) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("scurve requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(time)) time <- jmvcore::resolveQuo(jmvcore::enquo(time))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(time), time, NULL))


    options <- scurveOptions$new(
        dep = dep,
        time = time,
        model = model,
        eq = eq,
        par = par,
        aic = aic,
        aicc = aicc,
        bic = bic,
        r2 = r2,
        r2_adj = r2_adj,
        rmse = rmse,
        mae = mae,
        medae = medae,
        smape = smape,
        rrmse = rrmse,
        ogf = ogf,
        ogf_s = ogf_s,
        sndPlot = sndPlot,
        keyGrowth = keyGrowth,
        res = res,
        fPoints = fPoints,
        pPoints = pPoints,
        lagEnd = lagEnd,
        thVal = thVal,
        asymptote = asymptote)

    analysis <- scurveClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

